mapper.select:
SELECT
FROM (select ROW_NUMBER() OVER (ORDER BY m.mem_join_date) NUM, e.enrd_id as enrd_id, e.mem_id as mem_id, m.mem_phone as mem_phone, m.mem_name as mem_name, substr(enrd_complete_date,1,10) as enrd_complete_date, m.mem_email as mem_email, m.mem_company_name as mem_company_name, s.slb_name as slb_name, e.enrd_stat as enrd_stat, substr(enrd_join_date,1,10) as enrd_join_date FROM enrollment_detail E LEFT JOIN member M ON e.mem_id = m.mem_id LEFT JOIN course C ON e.crs_id = c.crs_id LEFT JOIN syllabus S ON c.slb_id = s.slb_id WHERE enrd_stat != '취소' ORDER BY enrd_id)
WHERE NUM BETWEEN #{pageStart}
AND #{perPageNum}
AND (#{perPageNum} + #{pageStart})
order by enrd_id


SELECT crs_id
FROM enrollment_detail
WHERE mem_id = #{memberVO.id} AND crs_id = #{courseVO.id}

mapper.update:
UPDATE enrollment_detail
enrd_stat = #{stat}, enrd_complete_date = #{completeDate},
WHERE enrd_id = #{id}


    	UPDATE enrollment_detail
		SET enrd_stat = '취소'
		WHERE enrd_id = #{id}


    	UPDATE enrollment_detail
		SET enrd_stat = '승인'
		WHERE enrd_id = #{id}


    	UPDATE enrollment_detail
		SET enrd_stat = '수료', enrd_complete_date = sysdate
		WHERE enrd_id = #{id}

mapper.select:
SELECT * FROM ( select ROW_NUMBER() OVER (ORDER BY m.mem_join_date) NUM, E.enrd_id, M.mem_id , M.mem_phone, M.mem_name, M.mem_email, M.mem_company_name, S.slb_name, E.enrd_stat, E.enrd_join_date, E.enrd_complete_date from enrollment_detail E LEFT JOIN member M ON e.mem_id = m.mem_id LEFT JOIN course C ON e.crs_id = c.crs_id LEFT JOIN syllabus S ON c.slb_id = s.slb_id ORDER BY enrd_id) WHERE NUM BETWEEN #{pageStart}
AND #{perPageNum}
AND (#{perPageNum} + #{pageStart}) -1
order by enrd_id
